import React from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";

const LoginForm = () => {
  const navigate = useNavigate();
  const [userName, setUserName] = React.useState("");
  const [password, setPassword] = React.useState("");

  const handleLogin = async () => {
    await axios
      .post("", { userName, password })
      .then((res) =>
        localStorage.setItem("accessToken", JSON.stringify(res.data))
      )
      .catch((err) => console(err));
    navigate("/");
  };
  return (
    <div className="w-full h-full flex justify-center items-center bg-gradient-to-tl from-login-pink via-login-blue to-login-green">
      <div className="text-center w-[400px] h-[300px] pt-[50px] px-10 border-2 border-white rounded-xl bg-[#ece9fe]/30 shadow-md">
        <h1 className="text-4xl p">Đăng nhập</h1>
        <div className="flex flex-col items-start">
          <label className="ml-1 mb-2" htmlFor="userName">Nhập tài khoản: </label>
          <input id="userName" className="w-full p-2 mb-4 rounded-xl"
            placeholder="username hoặc email"
            value={userName}
            onChange={(e) => setUserName(e.target.value)}
          />
          <label className="ml-1 mb-2" htmlFor="password">Nhập tài khoản: </label>
          <input id="password" className="w-full p-2 rounded-xl"
            placeholder="username hoặc email"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>  
      </div>
    </div>
  );
}
export default LoginForm;
